import React, { Component } from "react";
import classes from './SignUp.css';
import { Button, Form, Input } from 'element-react';
// import { Button } from '@material-ui/core';
import tick from "../../assets/img/navegador.svg";
import bgPink from '../../assets/img/bg-orange-left.svg'
import bgOrange from '../../assets/img/bg-pink-right.svg'
class SignUp extends Component {
    state = {
        loadingSignUp: false,
        sucessSignUp: false,
        form: {
            email: '',
            password: '',
        },
        rules: {
            email: [
                { required: true, message: "Por favor, seu email", trigger: 'change' }
            ],
            password: [
                { required: true, message: "Por favor, digite sua senha", trigger: 'change' },
            ],
        }
    };

    componentDidMount = () => {
    }

    onChange(key, value) {
        this.setState({
            form: Object.assign({}, this.state.form, { [key]: value })
        });
    }

    handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            this.handleSubmit(e);
        }
    };

    roteSignIn = (e) => {
        this.props.history.push({
            pathname: '/sign-in',
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        this.setState({ loadingSignUp: true });


        this.refs.form.validate((valid) => {
            if (valid) {
                this.setState({ sucessSignUp: true });
            } else {
                this.setState({ loadingSignUp: false });
            }
        });
    }

    render() {
        return (


            <div class="div-home-page full-page">

                <div class="div-20 move-left-animation">
                    <img src={bgPink}></img>
                </div>

                <div class="div-60">


                    <div class="info">
                        <h3 onClick={(e) => this.routeTo("")} class="info-text float-right">In√≠cio</h3>
                        <h3 onClick={(e) => this.routeTo("sign-up")} class="info-text float-right">Cadastrar</h3>
                        <h3 onClick={(e) => this.routeTo("about")} class="info-text float-right">Sobre</h3>
                    </div>

                    <div class="main-area">

                        <h1 class="title-home-page title-color">Sobre</h1>
                        <h2 class="sub-title-home-page title-color">o</h2>
                        <h1 class="title-home-page title-color">Projeto</h1>
                        <div class="barra-azul"></div>


                    </div>

                    <div class="div-20 move-right-animation align-right">
                        <img src={bgOrange}></img>
                    </div>
                </div>
            </div>



        );
    }
}

export default SignUp;
