import React, { Component } from "react";
import classes from './SignUp.css';
import { Button, Form } from 'element-react';
import { i18n } from 'element-react'
import locale from 'element-react/src/locale/lang/en'
class SignUp extends Component {
    state = {
        form: {
            email: '',
            password: '',
        },
        rules: {
            email: [
                { required: true, message: strings[this.props.language].type_your + " " + strings[this.props.language].email_address, trigger: 'change' }
            ],
            phone: [
                { required: true, message: strings[this.props.language].type_your + " " + strings[this.props.language].phone, trigger: 'change' },
                {
                    validator: (rule, value, callback) => {

                        let size = value.replace(/[^0-9]/g, "").length;
                        if (size < 10) {
                            callback(new Error(strings[this.props.language].please_phone));
                        }
                        else {
                            callback();
                        }
                    }, trigger: 'blur,change'
                }
            ],
        }
    };

    componentDidMount = () => {

    }

    render() {
        return (
            <div class="header">
                <div class="text-box">




                    <h1 class="heading-primary">
                        Cadastro
                    </h1>

                    <input type="text" name="name" />
                    <input type="text" name="name" />


                    <Button loading={true}>Cadastrar</Button>


                </div>
            </div>
        );
    }
}

export default SignUp;
